mapper.select:
/* getUserAccount */
SELECT USER_ID, USER_NM, USER_PW
FROM TB_MST_USER
WHERE USER_ID= @{userId}

/* getUserAuthorities */
SELECT ROLE_CD
FROM TB_MAP_ROLE
WHERE USER_ID = @{userId}

mapper.insert:
/* saveUser */
INSERT INTO TB_MST_USER (
  USER_ID, USER_PW, USER_NM, EMAIL
) VALUES (
  @{userId},@{userPw},@{userNm}, @{email}
)
